mapper.select:
SELECT id, spu_name, spu_description, catalog_id, brand_id, weight, publish_status, create_time, update_time FROM pms_spu_info
AND (spu_name LIKE CONCAT('%', #{params.key}, '%') OR pms_spu_info.spu_description LIKE CONCAT('%', #{params.key}, '%'))
AND catalog_id = #{params.catalogId}
AND brand_id = #{params.brandId}
AND publish_status = #{params.status}
