mapper.update:
UPDATE wms_ware_sku
SET stock = stock + #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}

mapper.select:
SELECT COUNT(*)
FROM wms_ware_sku
WHERE sku_id = #{skuId}
  AND ware_id = #{warId}

SELECT SUM(stock - stock_locked)
FROM wms_ware_sku
WHERE sku_id = #{skuId}

mapper.update:
UPDATE wms_ware_sku
SET stock_locked = stock_locked + #{count}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked >= #{count}

UPDATE wms_ware_sku
SET stock_locked = stock_locked - #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}
